$OU = "OU=CompassApp,OU=Compass,OU=Accounts,OU=Mount Clear Primary School,DC=curric,DC=mount-clear-ps,DC=wan"

New-ADGroup -Name CompassAppActivityProfileBase -GroupCategory Security -GroupScope Global -Path $OU -Description "Required to be able to search for and access Class Profiles in Compass as well as the Subjects and Classes Page."
