New-ADGroup -Name CompassAppAttendanceBase -GroupCategory Security -GroupScope Global -Path $OU -Description "Required to access the base level functionality in the Attendance module, including the Attendance dashboard, Attendance tabs on any profiles in Compass, and the ability to mark the roll."
New-ADGroup -Name CompassAppAttendanceMarkOtherRolls -GroupCategory Security -GroupScope Global -Path $OU -Description "Allows user to mark other users' rolls."
New-ADGroup -Name CompassAppAttendance.NotesApprover -GroupCategory Security -GroupScope Global -Path $OU -Description "Allows user to approve or delete attendance notes."
New-ADGroup -Name CompassAppBilling.Admin -GroupCategory Security -GroupScope Global -Path $OU -Description "This permission allows the user to manage the Administration of the Billing module."
New-ADGroup -Name CompassAppBilling.Base -GroupCategory Security -GroupScope Global -Path $OU -Description "Gives the user access to the Billing page and lets you view the dashboard & log tabs as well as viewing the billing schedule itself"
