New-ADGroup -Name CompassAppClassesPower -GroupCategory Security -GroupScope Global -Path $OU -Description "This allows users to edit enrolments for students of classes on the Custom Classes layer. This can be useful for IM classes, without giving the user Admin permissions."
New-ADGroup -Name CompassAppCodeSetAdministration -GroupCategory Security -GroupScope Global -Path $OU -Description "This permission gives staff access to Code Set Administration in Admin Tools"
New-ADGroup -Name CompassAppCommentBankBase -GroupCategory Security -GroupScope Global -Path $OU -Description "Required to access the Comment Bank menu item and dashboard."
New-ADGroup -Name CompassAppCommentBanksAdmin -GroupCategory Security -GroupScope Global -Path $OU -Description "This allows the user to add/edit/delete any entries in the School-Wide comment bank as well as their personal comment bank."
New-ADGroup -Name CompassAppCompassApiUser -GroupCategory Security -GroupScope Global -Path $OU -Description "Users with this permission will be able to access their individual API key."
