New-ADGroup -Name CompassAppEnrolmentsBase -GroupCategory Security -GroupScope Global -Path $OU -Description "This allows the user permission to view content of the Enrolments module (within Enrolments V2 only)."
New-ADGroup -Name CompassAppEnrolmentsV2Admin -GroupCategory Security -GroupScope Global -Path $OU -Description "With this the user can manage all features of the Enrolments V2 module - this module has not yet been released."
New-ADGroup -Name CompassAppEventsAdmin -GroupCategory Security -GroupScope Global -Path $OU -Description "This allows the user to create/edit/delete events and event templates."
New-ADGroup -Name CompassAppEventsBase -GroupCategory Security -GroupScope Global -Path $OU -Description "Required to access base level functionality in the Events module, including the item in the menu and Events dashboard."
