New-ADGroup -Name CompassAppGroupActivitiesAdmin -GroupCategory Security -GroupScope Global -Path $OU -Description "Allows user to create/edit/delete Group Activities on the Group Activities page."
New-ADGroup -Name CompassAppGroupProfileBase -GroupCategory Security -GroupScope Global -Path $OU -Description "Required to be able to search for and access Group Profiles in Compass, including Form Group, Year Level, House, Indigenous."
New-ADGroup -Name CompassAppGuardianAdmin -GroupCategory Security -GroupScope Global -Path $OU -Description "This allows the user to access the Guardian page and all of its features."
New-ADGroup -Name CompassAppGuardianUser -GroupCategory Security -GroupScope Global -Path $OU -Description "Provides staff with access to Guardian doors via their mobile."
New-ADGroup -Name CompassAppIdentityCardAdmin -GroupCategory Security -GroupScope Global -Path $OU -Description "Users with this permission will have access to the ID Card dashboard and be able to complete all card related functions available on the dashboard."
