New-ADGroup -Name CompassAppReportsPower -GroupCategory Security -GroupScope Global -Path $OU -Description "This allows the user to enter results for classes they are not associated with, when using the Semester Reports module"
New-ADGroup -Name CompassAppReportsView -GroupCategory Security -GroupScope Global -Path $OU -Description "This permission is no longer active."
